//Listen for submit
document.getElementById('taksonomic-form').addEventListener('submit', function(e){
  //Hide results
  document.getElementById('results').style.display = 'none';
  //Show loading
  document.getElementById('loading').style.display = 'inline';

  setTimeout(ShowResults, 2000);

  e.preventDefault();
});

//Calculate Results
function ShowResults(e){

  var x = document.getElementById('name').value;
  var y = document.getElementById('surname').value;

  if(x != "" && y != ""){
    //document.getElementById("taksTitle").innerHTML = "<?php echo $tax; ?>";
	//document.getElementById("taksTitle").innerHTML = "abc";
    //Show results
    document.getElementById('results').style.display = 'inline';
    //Hide loader
    document.getElementById('loading').style.display = 'none';

    document.getElementById('name').value = "";
    document.getElementById('surname').value = "";

  } else {
    showError('Pārbaudi, vai ir pareizi ievadīti visi lauki');
   //Hide loader
   document.getElementById('loading').style.display = 'none';
  }

 
}

//Show error
 function showError(error){
  //Create a div
  const errorDiv = document.createElement('div');
  //Get elements
  const card = document.querySelector('.card');
  const heading = document.querySelector('.heading');
  //Add class
  errorDiv.className = 'alert alert-danger';
  //Create text node and append to div
  errorDiv.appendChild(document.createTextNode(error));
  //Insert error above heading
  card.insertBefore(errorDiv, heading);
  //Clear error after 3 seconds
  setTimeout(clearError, 3000);
}

//Clear error
function clearError(){
  document.querySelector('.alert').remove();
}


function exportHTML(){

  var header = "<html xmlns:o='urn:schemas-microsoft-com:office:office' "+
       "xmlns:w='urn:schemas-microsoft-com:office:word' " +
       "xmlns='http://www.w3.org/TR/REC-html40'>" +
       `<head><meta charset='utf-8'><title>Export HTML to Word Document with JavaScript</title></head><body>
				<center><img style='display:block; width:100%; height:100%' src='data:image/jpeg;base64,'/></center>
                &nbsp;
                <center><h1 class="pt-5" style="font-size: 56px; font-style: italic;">Apliecinājums</h1></center>
                &nbsp;
                <center><h1 style="font-style: italic; font-size: 40px;">`;
       
  var footer = `</h1></center>

                <hr width=80% align=center style="height:1px;" noshade></hr>
                <center><h1 style="font-style: italic;">Par aktīvu piedalīšanos</h1>
                <center><center><h1 style="font-style: italic;">Zinātnienu Nakts 2019</h1>
                <center><center><h1 style="font-style: italic;">"Kultūras mantojums"</h1>
                <center><center><h1 style="font-style: italic;">Pasākuma organizētajās aktivitātēs</h1>            
                &nbsp;
                <center><center><h1 style="font-style: italic;">Latvijas Universitāte,</h1>
                <center><center><h1 style="font-style: italic;">Bioloģijas institūts, Salaspils</h1>
                &nbsp;
				<center><img style='display:block' src='data:image/png;base64,'/></center>
                </body></html>`;
            
  var sourceHTML = header+document.getElementById("taksTitle").innerHTML+footer;
  var source = 'data:application/vnd.ms-word;charset=utf-8,' + encodeURIComponent(sourceHTML);
  var fileDownload = document.createElement("a");
  document.body.appendChild(fileDownload);
  fileDownload.href = source;
  fileDownload.download = 'document.doc';
  fileDownload.click();
  document.body.removeChild(fileDownload);
} 

